function populateSchoolDropdown() {
  const select = document.getElementById("school");
  if (!select) return;

  // Keep the first option if it's a placeholder (value === "" or disabled)
  const existingOptions = Array.from(select.options);
  const keepPlaceholder =
    existingOptions.length > 0 &&
    (existingOptions[0].value === "" || existingOptions[0].disabled);

  // Clear
  select.innerHTML = "";

  if (keepPlaceholder) {
    const opt = document.createElement("option");
    opt.value = "";
    opt.textContent = "Select a law school";
    opt.disabled = true;
    opt.selected = true;
    select.appendChild(opt);
  }

  Object.keys(MHA_BY_SCHOOL)
    .sort((a, b) => a.localeCompare(b))
    .forEach((school) => {
      const opt = document.createElement("option");
      opt.value = school;
      opt.textContent = school;
      select.appendChild(opt);
    });
}

function setMhaFromSchool() {
  const select = document.getElementById("school");
  const mhaInput = document.getElementById("mha");
  if (!select || !mhaInput) return;

  const school = select.value;
  if (!school || !(school in MHA_BY_SCHOOL)) return;

  mhaInput.value = MHA_BY_SCHOOL[school];

  // Optional: highlight the MHA box if you already have a CSS class for it
  mhaInput.classList.add("highlight");
  setTimeout(() => mhaInput.classList.remove("highlight"), 600);
}

document.addEventListener("DOMContentLoaded", () => {
  populateSchoolDropdown();

  const select = document.getElementById("school");
  if (select) select.addEventListener("change", setMhaFromSchool);
});
